class Plugin:
    def __init__(self, matrix=True, show=False):
        super(Plugin, self).__init__()
        self.version = "0.0.2"
        self.author = "Infected"
        self.name = "Urlab Image"

        self.logo = Image(64, 16, pixmap=logo)
        print(self.logo)
        self.slide = Slide(self.logo, )
        self.screen = Screen(matrix=matrix, show=show)
        self.screen.add(self.slide, name="logo", refresh=True)



    def start(self):
        while True:
            self.screen.refresh()
            self.slide.refresh()
